<?phpfunction distFrom($latCur, $longCur, $latDest, $longDest) {	$rayonterre = 6371000.0; // radius of earth in meters	$dLat = deg2rad($latDest - $latCur);	$dLng = deg2rad($longDest - $longCur);		$a =	sin($dLat/2) * sin($dLat/2) +			cos(deg2rad($latCur)) * cos(deg2rad($latDest)) *			sin($dLng/2) * sin($dLng/2);	$c =	2 * atan2(sqrt($a), sqrt(1-$a));		$distance = $rayonterre * $c;		/*	 * Resultat plus petit ...	 */	// $sinLatD = sin($latDistRad);	// $sinLngD = sin($lngDistRad);	// $cosLat1 = cos(deg2rad($latDest));	// $cosLat2 = cos(deg2rad($latCur));		// $a = $sinLatD*$sinLatD + $cosLat1*$cosLat2*$sinLngD*$sinLngD*$sinLngD;	// if($a<0) $a = -1*$a;	// $c = 2*atan2(sqrt($a), sqrt(1-$a));		// $distance = $radius*$c;	return $distance;}?>